import Card from "../UI/Card";
import classes from "./Cart.module.css";
import CartItem from "./CartItem";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { cartActions } from "../store/cart-state";

const Cart = () => {
  const cartItemValues = useSelector((state) => state.cart);
  const dispatch = useDispatch();

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
        {cartItemValues.map((item) => {
          if (item.quantity < 1) {
            // console.log("ya es menos 1 compa");
            // return dispatch(
            //   cartActions.removeItem(cartItemValues.indexOf(item))
            // );
            return dispatch(cartActions.removeItem());
          }
          return (
            <CartItem
              key={cartItemValues.indexOf(item)}
              item={{
                title: item.title,
                quantity: item.quantity,
                total: 18,
                price: item.price,
              }}
              id={cartItemValues.indexOf(item)}
            />
          );
        })}
      </ul>
    </Card>
  );
};

// item={{ title: "Test Item", quantity: 3, total: 18, price: 6 }}
export default Cart;
