  //       item={{
  //         title: item.title,
  //         quantity: item.quantity,
  //         total: 18,
  //         price: item.price,
  //       }}
  //       id={cartItemValues.indexOf(item)}
  //     />
  //   );
  // });

  const cartItems = cartItemValues.filter((item) => {
    if (item.quantity < 1) {
      console.log("ya es menos 1 compa");
      dispatch(cartActions.removeItem(cartItemValues.indexOf(item)));
    }
    return (
      <CartItem
        key={cartItemValues.indexOf(item)}
        item={{
          title: item.title,
          quantity: item.quantity,
          total: 18,
          price: item.price,
        }}
        id={cartItemValues.indexOf(item)}
      />
    );
  });

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>{cartItems}</ul>
    </Card>
  );
};

// item={{ title: "Test Item", quantity: 3, total: 18, price: 6 }}
export default Cart;
